import math as m

def num2array(n):
    return [int(i) for i in str(n)]

def greatestProductThirteen(n):
    ans = num2array(n)
    size = len(ans)
    prod = 1
    count = 0
    while(count != (size - 14)):
        temp = ans[count] * ans[count+1] * ans[count+2] * ans[count+3] * ans[count+4] * ans[count+5] * ans[count+6] * ans[count+7] * ans[count+8] * ans[count+9] * ans[count+10] * ans[count+11] * ans[count+12]
        if(temp > prod):
            prod = temp
        count = count + 1
    return prod

print(greatestProductThirteen())
